    img {
        width: 100%;
        height: auto;
        margin: 10px;
        @media (max-width: 768px) {
            width: 100%;
            height: auto;
            margin: 5px;
        }
    }

    img:nth-child(1) {100% {margin: 0px; widthStyleColor: #fff;}}   // defaults to transparentToValue: 0px
