import {Home} from './components/Home/Home.js';
import React, { useState} from 'react';
import {Match} from './components/Match/Match.js';
import styled from 'styled-components';
import { GlobalStyle } from "./styles/global";

const Container = styled.div`
  background-color: black;
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
`
const CardHome = styled.div`
  width: 25%;
  height: 80vh;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  border: 4px solid black;
  border-radius: 12px;
  background-color: white;
`
const HeaderCard = styled.header`
  display: flex;
  justify-content: space-around;
  width: 100%;
  height: 10vh;
  img{
    width: 60px;
    height: 60px;
  }
  :hover h2{
    color: red;
  }
`

function App() {

  const [screen, setScreen] = useState('start');

  const changeScreen = ()=>{
    switch(screen){
      case"homePage":
      return <Home/>;
      case"matchList":
      return <Match/>;
      default:
        return <Home/>
    }
  }
  const goToHomePage = ()=>{
    setScreen("homePage");
  }
  const goToMatchList = ()=>{
    setScreen("matchList");
  }

  return (
    <Container>
      <CardHome>
        <HeaderCard>
          <img src="https://img.icons8.com/doodle/48/000000/--tinder.png" onClick={goToHomePage} 
          alt="congig"
          />
          <h2>ASTROMATCH</h2>
          <img src="https://img.icons8.com/ios/50/000000/overview-pages-4.png" onClick={goToMatchList}
          alt='match'/>
        </HeaderCard>
        {changeScreen()}
      </CardHome>
    </Container>
  );
};

export default App;
