import React, { useState} from 'react';
import Post from '../Post/Post';
import styled from 'styled-components';

const MainContainer = styled.div`  
    display: flex;
    justify-content: center;
    flex-direction: column;
    align-items: center;
`

function NovaArray() {
    // estados
    const[inputName,setInputName]=useState(""); 
    const [inputUsuario, setInputUsuario] = useState('');
    const [inputPost, setInputPost] = useState('');
    const [pessoas, setPessoas] = useState([
    { nomeUsuario:'paulinha',
      fotoUsuario:'https://picsum.photos/50/55',
      fotoPost:'https://picsum.photos/20/15'},
    { nomeUsuario:'patricia',
      fotoUsuario:'https://picsum.photos/50/50',
      fotoPost:'https://picsum.photos/300/150'},
    { nomeUsuario:'marcos',
      fotoUsuario:'https://picsum.photos/536/354',
      fotoPost:'https://picsum.photos/400/150'}
  ]);

  // eventos
  const addNome = (e)=>{
    setInputName(e.target.value);
    console.log(inputName);
  }

   const handleInputUsuario = (e) => {
    setInputUsuario(e.target.event);
    console.log(inputUsuario);
   }
   const handleInputPost = (e) => {
    setInputPost(e.target.event);
    console.log(inputPost);
    }

  const NovaLista = pessoas.map((elemento, index) => {
    return <Post
    nomeUsuario={elemento.nomeUsuario}
    fotoUsuario={elemento.fotoUsuario}
    fotoPost={elemento.fotoPost}
    key={index}/>
   })

   // adicionar novo post
    
        const novoPost = [
            {nomeUsuario: inputNome, 
            fotoUsuario: inputUsuario, 
            fotoPost: inputPost}, ...pessoas]

        setPessoas(novoPost);


        const addPost = (e)=>{
            e.preventDefault();

            const addArrayPosts = [...pessoas,{nomeUsuario:inputName,fotoUsuario:inputPerfil,fotoPost:inputPoster}]
            setPosts(addArrayPosts)

        setInputName("")
        setInputUsuario('')
        setInputPost('')
   }

return(
  <MainContainer>
    <form>
    <input type='text' required placeholder='Nome Usuario' value={inputName} onChange={addNome}/>

        <input
            type="text"
            required placeholder='foto do perfil'
            value={inputUsuario}
            onChange={handleInputUsuario}/>
        <input
            type="text"
            placeholder='foto do post'
            value={inputPost}
            onChange={handleInputPost}/>
        <button onClick={addPost}>adicionar</button>
    </form>

    {NovaLista}
    {console.log(pessoas)}

  </MainContainer>
)

}

export default NovaArray;
