import React, { useState} from 'react';
import Post from '../Post/Post';
import styled from 'styled-components';

const MainContainer = styled.div`  
    display: flex;
    justify-content: center;
    flex-direction: column;
    align-items: center;
`

function NovaArray() {

    const [nome, setNome] = useState('');
    const [usuario, setUsuario] = useState('');
    const [post, setPost] = useState('');
    

  const pessoa = [
    { nomeUsuario:'paulinha',
      fotoUsuario:'https://picsum.photos/50/55',
      fotoPost:'https://picsum.photos/20/15'},
    { nomeUsuario:'patricia',
      fotoUsuario:'https://picsum.photos/50/50',
      fotoPost:'https://picsum.photos/300/150'},
    { nomeUsuario:'marcos',
      fotoUsuario:'https://picsum.photos/536/354',
      fotoPost:'https://picsum.photos/400/150'}
  ]

  const handleInputUsuario = (e) => {
    setNome(e.target.event)
   }
   const handleInputfotoUsuario = (e) => {
    setUsuario(e.target.event)
   }
   const handleInputfotoPost = (e) => {
    setPost(e.target.event)
    }

  const NovaLista = pessoa.map((elemento, index) => {
    return <Post
    nomeUsuario={elemento.nomeUsuario}
    fotoUsuario={elemento.fotoUsuario}
    fotoPost={elemento.fotoPost}
    key={index}
    />
   })

   const addPost = () => {

        const novoPost = {nome: nome, usuario: usuario, post: post}
        const novaLista = [...pessoa, novoPost]
        setPessoa(novaLista)
   }

return(
  <MainContainer>
    <form>
        <label></label>
        <input
            placeholder='nome do usuario'
            onChange={handleInputUsuario}
            value={nome}
        ></input>

        <label></label>
        <input
            placeholder='foto do usuario'
            onChange={handleInputfotoUsuario}
            value={usuario}
        ></input>

        <label></label>
        <input
            placeholder='foto do post'
            onChange={handleInputfotoPost}
            value={post}
        ></input>

        <button onClick={addPost}>adicionar</button>
    </form>

    {NovaLista}
    {console.log(pessoa)}

  </MainContainer>
)

}

export default NovaArray;
