import React, { useState} from 'react';
import Post from '../Post/Post';
import styled from 'styled-components';

const MainContainer = styled.div`  
    display: flex;
    justify-content: center;
    flex-direction: column;
    align-items: center;
`

function NovaArray() {
    // estados
    const [inputNome, setInputNome] = useState('');
    const [inputUsuario, setInputUsuario] = useState('');
    const [inputPost, setInputPost] = useState('');
    const [pessoas, setPessoas] = useState([
    { nomeUsuario:'paulinha',
      fotoUsuario:'https://picsum.photos/50/55',
      fotoPost:'https://picsum.photos/20/15'},
    { nomeUsuario:'patricia',
      fotoUsuario:'https://picsum.photos/50/50',
      fotoPost:'https://picsum.photos/300/150'},
    { nomeUsuario:'marcos',
      fotoUsuario:'https://picsum.photos/536/354',
      fotoPost:'https://picsum.photos/400/150'}
  ]);

  // eventos
  const handleInputNome = (e) => {
    setInputNome(e.target.event);
    console.log(inputNome);
   }
   const handleInputUsuario = (e) => {
    setInputUsuario(e.target.event);
    console.log(inputUsuario);
   }
   const handleInputPost = (e) => {
    setInputPost(e.target.event);
    console.log(inputPost);
    }

  const NovaLista = pessoas.map((elemento, index) => {
    return <Post
    nomeUsuario={elemento.nomeUsuario}
    fotoUsuario={elemento.fotoUsuario}
    fotoPost={elemento.fotoPost}
    key={index}
    />
   })

   // adicionar novo post
   const addPost = (e) => {
    e.preventDefault();
    
        const novoPost = {nomeUsuario: inputNome, usuario: inputUsuario, post: inputPost}
        const novaLista = [novoPost, ...pessoas]
        setPessoas(novaLista)
   }

return(
  <MainContainer>
    <form>
        <label></label>
        <input
            placeholder='nome do usuario'
            onChange={handleInputNome}
            value={inputNome}
        ></input>

        <label></label>
        <input
            placeholder='foto do usuario'
            onChange={handleInputUsuario}
            value={inputUsuario}
        ></input>

        <label></label>
        <input
            placeholder='foto do post'
            onChange={handleInputPost}
            value={inputPost}
        ></input>

        <button onClick={addPost}>adicionar</button>
    </form>

    {NovaLista}

  </MainContainer>
)

}

export default NovaArray;
