import React from 'react';
import Post from '../Post/Post';
import styled from 'styled-components';

export const MainContainer = styled.div`  
    display: flex;
    justify-content: center;
    flex-direction: column;
    align-items: center;
`

function NovaArray() {

  const pessoa = [
    { nomeUsuario:'paulinha',
      fotoUsuario:'https://picsum.photos/50/55',
      fotoPost:'https://picsum.photos/20/15'},
    { nomeUsuario:'patricia',
      fotoUsuario:'https://picsum.photos/50/50',
      fotoPost:'https://picsum.photos/300/150'},
    { nomeUsuario:'marcos',
      fotoUsuario:'https://picsum.photos/536/354',
      fotoPost:'https://picsum.photos/400/150'}
  ]

  const NovaLista = pessoa.map((elemento, index) => {
    return <Post
    nomeUsuario={elemento.nomeUsuario}
    fotoUsuario={elemento.fotoUsuario}
    fotoPost={elemento.fotoPost}
    key={index}
    />
   })

   handleInputUsuario = (e) => {
    e.target.event 
   }

   handleInputfotoUsuario = (e) => {
    e.target.event
   }
    handleInputfotoPost = (e) => {
    e.target.event
    }

return(
  <MainContainer>
    <form>
        <label></label>
        <input
            placeholder='nome do usuario'
            onChange={handleInputusuario}
        ></input>

        <label></label>
        <input
            placeholder='foto do usuario'
            onChange={handleInputfotoUsuario}
        ></input>

        <label></label>
        <input
            placeholder='foto do post'
            onChange={handleInputfotoPost}
        ></input>

        <button>Enviar</button>
    </form>

    {NovaLista}
    {console.log(pessoa)}
  </MainContainer>
)

}

export default NovaArray;
